    private void awal()
    {
        kodesuplier.setText("");
        namasuplier.setText("");
        alamatsuplier.setText("");
        teleponsuplier.setText("");
        c_suplier csuplier = new c_suplier();
        ArrayList<MODEL.m_suplier> suplier = csuplier.selecdata();
        DefaultTableModel table = (DefaultTableModel)TABEL_su.getModel();
        table.setRowCount(0);
        for (m_suplier row : suplier)
        {
            Object[] o = new Object[6];
            o[0] = row.getKodesuplier();
            o[1] = row.getNamasuplier();
            o[2] = row.getAlamatsuplier();
            o[3] = row.getTeleponsuplier();
            table.addRow(o);
        }
    }
    
    private void HAPUSActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_HAPUSActionPerformed
        int index = TABEL_su.getSelectedRow();
        m_suplier msuplier = new m_suplier();
        msuplier.setKodesuplier(TABEL_su.getValueAt(index, 0).toString());
        c_suplier csuplier = new c_suplier();
        csuplier.deletedata(msuplier);
        JOptionPane.showMessageDialog(null, "Data berhasil dihapus");
        awal();
    }//GEN-LAST:event_HAPUSActionPerformed

