    private void SIMPANActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SIMPANActionPerformed
        if(kodesuplier.getText().equals("")||
                namasuplier.getText().equals("")||
                alamatsuplier.getText().equals("")||
                teleponsuplier.getText().equals(""))
        {  
            JOptionPane.showMessageDialog(null, "Data belum lengkap");
        }
        else
        {
            m_suplier data = new m_suplier ();
            data.setKodesuplier(kodesuplier.getText());
            data.setNamasuplier(namasuplier.getText());
            data.setAlamatsuplier(alamatsuplier.getText());
            data.setTeleponsuplier(teleponsuplier.getText());
            c_suplier csuplier = new c_suplier();
            csuplier.insertdata(data);
            this.awal();
        }
    }//GEN-LAST:event_SIMPANActionPerformed

