    private void TABEL_suMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_TABEL_suMouseClicked
        int index = TABEL_su.getSelectedRow();
        kodesuplier.setText(TABEL_su.getValueAt(index, 0).toString());
        namasuplier.setText(TABEL_su.getValueAt(index, 1).toString());
        alamatsuplier.setText(TABEL_su.getValueAt(index,2).toString());
        teleponsuplier.setText(TABEL_su.getValueAt(index, 3).toString());
    }//GEN-LAST:event_TABEL_suMouseClicked

